function timeAgo(e) {
  var t = (new Date).getTime() - e.getTime(),
    s = Math.floor(t / 1e3),
    i = Math.floor(s / 60),
    a = Math.floor(i / 60),
    r = Math.floor(a / 24),
    n = Math.floor(r / 30);
  return 0 === t ? "Just now" : s < 60 ? s + " seconds Ago" : i < 60 ? i + " minutes Ago" : a < 24 ? a + " hours Ago" : r < 30 ? r + " days Ago" : n < 12 ? n + " months Ago" : Math.floor(n / 12) + " years Ago"
}

function createSeriesWithChapter(posts, element) {
  const regexChapter = /((chapter|episode|ep|ch|vol|volume|bab)(\.\s|\s|\.)(\d{1,4})(\.\d{1,2})?(\s\-\s(fix))?)/gi;
  const noImage = 'https://blogger.googleusercontent.com/img/b/R29vZ2xl/AVvXsEhN74CcBgED5650EPwhm1ir78WYoOu10z_7Ww1lwW5u6ZNl2Ve9MK1DVsXE_O4svDpMGCDUrcYYYpi0sqzewLUtJzsysSEtqCru41FhWJeeqgxoAq-Hlh10PJYdbA35UczO4gQuuDBw0X6UULZkXZ3aTiu-n-R34tXtO4csVS6UtHTnlLSb6EB5ByvCIxfi/s800-rw/anime-girls-404-not-found-glowing-eyes-girls-frontline-wallpaper-preview.webp';
  const hotFilter = ['H', 'hot'];
  const nsfwFilter = ['nsfw', 'NSFW', 'Action']
  let html = '';

  posts.length >= 1 &&
    posts.forEach((post) => {
      ///Default Variable
      const title = post.title;
      const link = post.link;
      const image = post.image || noImage;

      //Semua Label List
      const labelList = post.label.map((label) => {
        return `<a href="${label}">${label}</a>`;
      }).join(', ');

      //hot label
      const hot = post.label
        .filter((i) => hotFilter.some(h => h == i))
        .map((label) => {
          return `<span class="hot">${label}</span>`;
        });

      //nsfwClass
      const nsfwClass = post.label.some(label => nsfwFilter.some(n => n == label)) ? 'nsfw-class' : '';

      //Chapter list
      const chapters = post.chapters
        .map((chapter) => {

          //Replace chapterTitle
          const chapterTanggal = chapter.date;
          const timeago = timeAgo(new Date(chapter.published));
          const chapterLink = chapter.link;
          const chapterTitle = chapter.title
            .match(regexChapter)
            .join(' ')
            .replace(/((chapter)(\.\s|\s|\.))/i, 'Ch.')
            .replace(/((episode)(\.\s|\s|\.))/i, 'Ep.')
            .replace(/((volume)(\.\s|\s|\.))/i, 'Vol.');

          return `<li class="flex justify-between items-center rounded-xl mb-1 bg-(--tint-80) text-gray-700 dark:text-gray-400 dark:bg-(--shade-80) px-1 hover:text-accent duration-150 hover:bg-slate-200 hover:dark:bg-gray-900"><a href="${chapterLink}">${chapterTitle}</a><span>${timeago}</span></li>`;
        })
        .join('');

      html += `<figure class="grid grid-cols-[123px_1fr] bg-(--tint-94) dark:bg-(--shade-90) rounded border border-(--shade-40) dark:border-(--shade-80)">
		<a class="pb-[135%] relative overflow-hidden rounded" href="${link}">
  		<img class="absolute inset-0 w-full h-full object-cover" loading="lazy" src="${image}" alt="Thumbnail">
		</a>
	<figcaption class="p-4">
      <div>${hot}</div>
      <h3 class="line-clamp-1 font-semibold"><a class="text-gray-800 dark:text-gray-100" href="${link}">${title}</a></h3>
      <div class="hidden">${labelList}</div>
      <div>${nsfwClass}</div>
      <ul>${chapters}</ul>
	</figcaption>
      </figure>`;
    });

  element && (element.innerHTML = html);
}
