div.script:
/**/
  const layoutElement = document.getElementById('layout-view');
  const buttons = document.querySelectorAll('#bl, #bt'); // Menggunakan ID sebagai selektor

  // Mendapatkan kelas yang tersimpan di localStorage (jika ada)
  const savedClass = localStorage.getItem('layoutClass');
  if (savedClass) {
    layoutElement.className = savedClass;
  }

  buttons.forEach((button) => {
    button.addEventListener('click', () => {
      layoutElement.className = '';
      const classNames = button.dataset.class.split(' '); // Memisahkan nilai-nilai kelas yang terpisah oleh spasi
      classNames.forEach((className) => {
        layoutElement.classList.add(className); // Menambahkan setiap nilai kelas ke elemen layout
      });

      // Menyimpan kelas yang dipilih ke localStorage
      localStorage.setItem('layoutClass', button.dataset.class);
    });
  });
