/**/
    class MangaReader {
        constructor(options) {
            this.options = options;
            this.currentMode = this.loadSavedMode() || options.mode;
            this.currentIndex = 0;
            this.loadedImages = new Map();
            this.preloadCount = 3;
            this.fitMode = this.loadSavedFitMode() || 'fit-both';
            this.fitModes = {
                'fit-both': {
                    icon: '<path fill="currentColor" d="m9.5 13.09 1.41 1.41-4.5 4.5H10v2H3v-7h2v3.59zm1.41-3.59L9.5 10.91 5 6.41V10H3V3h7v2H6.41zm3.59 3.59 4.5 4.5V14h2v7h-7v-2h3.59l-4.5-4.5zM13.09 9.5l4.5-4.5H14V3h7v7h-2V6.41l-4.5 4.5z"></path>',
                    label: 'Fit Both'
                },
                'fit-width': {
                    icon: '<path fill="currentColor" d="M17 3H7c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h10c1.1 0 2-.9 2-2V5c0-1.1-.9-2-2-2zm0 16H7V5h10v14z"></path>',
                    label: 'Fit Width'
                },
                'fit-height': {
                    icon: '<path fill="currentColor" d="M19 12h-2v3h-3v2h5v-5zM7 9h3V7H5v5h2V9zm14-6H3c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h18c1.1 0 2-.9 2-2v-14c0-1.1-.9-2-2-2zm0 16.01H3V4.99h18v14.02z"></path>',
                    label: 'Fit Height'
                },
                'no-limit': {
                    icon: '<path fill="currentColor" d="M3 5v4h2V5h4V3H5c-1.1 0-2 .9-2 2zm2 10H3v4c0 1.1.9 2 2 2h4v-2H5v-4zm14 4h-4v2h4c1.1 0 2-.9 2-2v-4h-2v4zm0-16h-4v2h4v4h2V5c0-1.1-.9-2-2-2z"></path>',
                    label: 'No Limit'
                }
            };
            this.modes = {
                'vertical': {
                    icon: '<path fill="currentColor" d="M4 14h16v2H4v-2zm0-4h16v2H4v-2zm0-4h16v2H4V6zm0-4h16v2H4V2z"/>',
                    label: 'Vertical Scroll'
                },
                'ltr': {
                    icon: '<path fill="currentColor" d="M14 7l-5 5 5 5V7z"/>',
                    label: 'Left to Right'
                },
                'rtl': {
                    icon: '<path fill="currentColor" d="M10 17l5-5-5-5v10z"/>',
                    label: 'Right to Left' 
                }
            };
            this.totalPages = options.pages.length;

            // Update total pages display immediately
            const totalPagesElement = document.getElementById('zeist-total-pages');
            if (totalPagesElement) {
                totalPagesElement.textContent = this.totalPages;
            }

            this.progressElement = document.getElementById('zeist-progress');
            this.readerId = 'zeist-manga-reader';
            this.init();
            this.initializeControls();
            this.initializeProgress(); // Tambahkan inisialisasi progress
        }

        loadSavedMode() {
            const savedMode = localStorage.getItem('manga-reader-mode');
            if (savedMode && ['vertical', 'ltr', 'rtl'].includes(savedMode)) {
                return savedMode;
            }
            return null;
        }

        saveMode(mode) {
            localStorage.setItem('manga-reader-mode', mode);
        }

        loadSavedFitMode() {
            return localStorage.getItem('manga-reader-fit-mode');
        }

        saveFitMode(mode) {
            localStorage.setItem('manga-reader-fit-mode', mode);
        }

        initializeControls() {
            // Initialize mode toggle
            const modeToggle = document.getElementById('zeist-mode-toggle');
            modeToggle.addEventListener('click', () => {
                const modes = Object.keys(this.modes);
                const currentIndex = modes.indexOf(this.currentMode);
                const nextIndex = (currentIndex + 1) % modes.length;
                this.setMode(modes[nextIndex]);
                this.updateModeContent();
            });

            // Update initial mode content
            this.updateModeContent();

            // Initialize fit toggle
            const fitToggle = document.getElementById('zeist-fit-toggle');
            fitToggle.addEventListener('click', () => {
                const modes = Object.keys(this.fitModes);
                const currentIndex = modes.indexOf(this.fitMode);
                const nextIndex = (currentIndex + 1) % modes.length;
                this.fitMode = modes[nextIndex];
                this.saveFitMode(this.fitMode);
                this.updateToggleContent();
                this.updateImageFit();
            });

            // Initialize navigation
            const leftNav = document.getElementById('zeist-nav-left');
            const rightNav = document.getElementById('zeist-nav-right');

            leftNav.onclick = (e) => {
                e.stopPropagation();
                this.navigate(this.currentMode === 'rtl' ? 1 : -1);
            };

            rightNav.onclick = (e) => {
                e.stopPropagation();
                this.navigate(this.currentMode === 'rtl' ? -1 : 1);
            };
        }

        updateModeContent() {
            const mode = this.modes[this.currentMode];
            const modeIcon = document.getElementById('zeist-mode-icon');
            const modeLabel = document.getElementById('zeist-mode-label');
            modeIcon.innerHTML = mode.icon;
            modeLabel.textContent = mode.label;
        }

        updateToggleContent() {
            const mode = this.fitModes[this.fitMode];
            const fitIcon = document.getElementById('zeist-fit-icon');
            const fitLabel = document.getElementById('zeist-fit-label');
            fitIcon.innerHTML = mode.icon;
            fitLabel.textContent = mode.label;
        }

        updateImageFit() {
            const images = document.querySelectorAll('.manga-page');
            images.forEach(img => {
                img.className = `manga-page img-${this.fitMode}`;
            });
        }

        init() {
            console.log('Reader initialized with options:', this.options);
            this.displayImages();
        }

        async preloadImage(url) {
            if (this.loadedImages.has(url)) return this.loadedImages.get(url);

            const img = new Image();
            const loadPromise = new Promise((resolve, reject) => {
                img.onload = () => resolve(img);
                img.onerror = reject;
            });
            img.src = url;
            this.loadedImages.set(url, loadPromise);
            return loadPromise;
        }

        async displayImages() {
            const container = document.createElement('div');
            container.id = 'manga-container';
            container.className = `manga-container ${this.currentMode}-mode`;
            container.innerHTML = '';

            if (this.currentMode === 'vertical') {
                // Add scroll event listener for vertical mode
                container.addEventListener('scroll', () => {
                    this.handleVerticalScroll(container);
                });

                // Add intersection observer for vertical mode
                const observer = new IntersectionObserver((entries) => {
                    entries.forEach(entry => {
                        const index = parseInt(entry.target.dataset.pageIndex);
                        if (entry.isIntersecting && index > this.currentIndex) {
                            this.currentIndex = index;
                            this.updateProgress();
                        }
                    });
                }, {
                    root: container,
                    threshold: 0.5 // Image is considered "read" when 50% visible
                });

                // Create and append images
                for (let i = 0; i < this.options.pages.length; i++) {
                    const img = document.createElement('img');
                    img.className = `manga-page img-${this.fitMode}`;
                    img.loading = i < 2 ? 'eager' : 'lazy';
                    img.src = this.options.pages[i];
                    img.dataset.pageIndex = i;
                    container.appendChild(img);
                    observer.observe(img);
                }
            } else {
                // LTR/RTL mode - display current image with navigation
                const pageContainer = document.createElement('div');
                pageContainer.className = 'manga-page-container';
                pageContainer.style.display = 'flex';
                pageContainer.style.width = '100%';
                pageContainer.style.height = '100%';
                pageContainer.style.alignItems = 'center';
                pageContainer.style.justifyContent = 'center';
                pageContainer.style.position = 'relative';

                const isRTL = this.currentMode === 'rtl';
                const leftNav = document.createElement('div');
                const rightNav = document.createElement('div');

                leftNav.className = 'nav-overlay nav-left';
                rightNav.className = 'nav-overlay nav-right';

                // Update navigation handlers
                leftNav.onclick = (e) => {
                    e.stopPropagation();
                    this.navigate(isRTL ? 1 : -1);
                };
                rightNav.onclick = (e) => {
                    e.stopPropagation();
                    this.navigate(isRTL ? -1 : 1);
                };

                // Create image container
                const imgContainer = document.createElement('div');
                imgContainer.style.flex = '1';
                imgContainer.style.height = '100%';
                imgContainer.style.display = 'flex';
                imgContainer.style.alignItems = 'center';
                imgContainer.style.justifyContent = 'center';

                // Load and display current image
                const currentImg = await this.preloadImage(this.options.pages[this.currentIndex]);
                const img = currentImg.cloneNode(false);
                img.className = `manga-page img-${this.fitMode}`;

                imgContainer.appendChild(img);

                // Assemble page container
                pageContainer.appendChild(leftNav);
                pageContainer.appendChild(imgContainer);
                pageContainer.appendChild(rightNav);

                // Set direction based on mode
                container.style.direction = isRTL ? 'rtl' : 'ltr';
                container.appendChild(pageContainer);
            }

            // Remove existing container if any
            const existingContainer = document.querySelector('#' + this.readerId + ' #manga-container');
            if (existingContainer) {
                existingContainer.remove();
            }

            document.getElementById(this.readerId).appendChild(container);
            this.preloadCurrentSet(); // Preload next images
        }

        handleVerticalScroll(container) {
            if (this.currentMode !== 'vertical') return;

            const images = container.getElementsByClassName('manga-page');
            const containerHeight = container.clientHeight;
            const scrollTop = container.scrollTop;
            const totalHeight = container.scrollHeight;

            // Calculate more accurate current index based on scroll position
            const scrollRatio = scrollTop / (totalHeight - containerHeight);
            const estimatedIndex = Math.floor(scrollRatio * images.length);

            // Find visible images and determine current page more accurately
            let mostVisibleIndex = this.currentIndex;
            let maxVisibleArea = 0;

            for (let i = Math.max(0, estimatedIndex - 2); 
                 i < Math.min(images.length, estimatedIndex + 3); i++) {
                const img = images[i];
                const rect = img.getBoundingClientRect();
                const visibleHeight = Math.min(rect.bottom, containerHeight) - 
                                    Math.max(rect.top, 0);

                if (visibleHeight > maxVisibleArea) {
                    maxVisibleArea = visibleHeight;
                    mostVisibleIndex = i;
                }
            }

            if (mostVisibleIndex !== this.currentIndex) {
                this.currentIndex = mostVisibleIndex;
                this.updateProgress();
            }

            // Update progress bar fill
            const fill = this.progressElement.querySelector('.progress-fill');
            fill.style.width = `${scrollRatio * 100}%`;
        }

        async preloadCurrentSet() {
            const startIdx = Math.max(0, this.currentIndex - 1);
            const endIdx = Math.min(this.options.pages.length, this.currentIndex + this.preloadCount);

            return Promise.all(
                this.options.pages
                    .slice(startIdx, endIdx)
                    .map(url => this.preloadImage(url))
            );
        }

        async navigate(direction) {
            const newIndex = this.currentIndex + direction;
            if (newIndex >= 0 && newIndex < this.options.pages.length) {
                this.currentIndex = newIndex;
                await this.displayImages();
                this.updateProgress();
            }
        }

        setMode(mode) {
            if (this.currentMode === mode) return;

            console.log('Changing mode to:', mode);
            this.currentMode = mode;
            this.currentIndex = 0; // Reset index when changing modes
            this.saveMode(mode); // Save mode preference
            this.clearContainer();
            this.displayImages();
            this.updateProgress();
        }

        clearContainer() {
            const container = document.querySelector('#' + this.readerId + ' #manga-container');
            if (container) {
                container.remove();
            }
        }

        initializeProgress() {
            if (!this.progressElement) return;

            const totalPages = this.options.pages.length;

            // Update total pages
            const totalPagesElement = this.progressElement.querySelector('#zeist-total-pages');
            if (totalPagesElement) {
                totalPagesElement.textContent = totalPages;
            }

            // Add click handler for progress bar
            const bar = this.progressElement.querySelector('#zeist-progress-bar');
            if (bar) {
                bar.addEventListener('click', (e) => {
                    const rect = bar.getBoundingClientRect();
                    const ratio = (e.clientX - rect.left) / rect.width;
                    const pageIndex = Math.floor(ratio * this.options.pages.length);

                    if (this.currentMode === 'vertical') {
                        const container = document.getElementById('zeist-manga-container');
                        if (container) {
                            const targetImage = container.children[pageIndex];
                            if (targetImage) {
                                targetImage.scrollIntoView({
                                    behavior: 'smooth',
                                    block: 'start'
                                });
                            }
                        }
                    } else {
                        this.navigateToPage(pageIndex);
                    }
                });
            }

            // Initialize dividers
            const dividers = this.progressElement.querySelector('#zeist-dividers');
            if (dividers) {
                dividers.innerHTML = this.options.pages
                    .map((_, i) => `<div class="progress-divider${i === 0 ? ' current' : ''}" data-page="${i + 1}"></div>`)
                    .join('');
            }
        }

        updateProgress() {
            if (!this.progressElement) return;

            const totalPages = this.options.pages.length;
            const progress = ((this.currentIndex + 1) / totalPages) * 100;

            // Update progress fill
            const fill = this.progressElement.querySelector('.progress-fill');
            if (fill) {
                fill.style.width = `${progress}%`;
            }

            // Update page numbers
            const currentPage = document.getElementById('zeist-current-page');
            if (currentPage) {
                currentPage.textContent = this.currentIndex + 1;
            }

            // Update dividers
            const dividers = this.progressElement.querySelectorAll('.progress-divider');
            dividers.forEach((divider, index) => {
                divider.classList.toggle('read', index < this.currentIndex);
                divider.classList.toggle('current', index === this.currentIndex);
            });
        }

        navigateToPage(index) {
            if (index >= 0 && index < this.options.pages.length) {
                this.currentIndex = index;

                if (this.currentMode === 'vertical') {
                    const container = document.querySelector('#' + this.readerId + ' #manga-container');
                    const targetImage = container.children[index];
                    if (targetImage) {
                        // Scroll to target image with smooth animation
                        targetImage.scrollIntoView({
                            behavior: 'smooth',
                            block: 'start'
                        });
                    }
                } else {
                    // Handle LTR/RTL navigation
                    this.displayImages();
                }

                this.updateProgress();
            }
        }
    }
