    // Get image URLs
    const _link_manga_hr = [...post_body.matchAll(/<img[^>]+src="([^"]+)"/g)]
        .map(match => match[1]);

    if (_link_manga_hr.length === 0) {
        console.error('No images found in post body');
        return;
