(function (a, b) {
  const storageType = sessionStorage || sessionStorage;
  const series = a.querySelectorAll('[data-series]');

  const config = {
    seriesLabel: 'Series',
    chapterLabel: 'Chapter',
    host: document.location.hostname,
    imageSize: 's380',
  };

  const chapters = storageType.getItem('chapterArr')
    ? JSON.parse(storageType.getItem('chapterArr'))
    : { items: [], max: 0 };

  if (series.length == 0) return;

  function getLabelTitle(e, d) {
    d = d.split(/[^a-zA-Z]/).filter((k) => k.length != 0);
    return e.find((e) => {
      let m = e.split(/[^a-zA-Z]/).filter((k) => k.length != 0);
      if (
        ((d.length == 1 && m.length == 1) ||
          d.length == m.length ||
          (d.length > 3 && m.length > 3)) &&
        d[0] == m[0]
      )
        return true;
    });
  }

  (async () => {
    if (chapters.items.length == 0) {
      const conf = {
        jumlah: 150,
        label: config.chapterLabel,
        host: config.host,
      };
      await new Promise((res) => {
        const bS = new BloggerScript(conf);
        let t = bS.createURL().href;
        let c = bS.getXHRtype(t);
        bS[c](t, (e) => {
          chapters.max = e.feed.openSearch$totalResults.$t;

          const entry = bS.getFeed(e);

          entry.forEach((chapter) =>
            chapters.items.push({
              title: chapter.title,
              link: chapter.link,
              id: chapter.id,
              label: chapter.label,
              date: chapter.date,
              published: chapter.published
            })
          );
          res();
        });
      });
    }

    for (el of series) {
      const customLabel = el.getAttribute('data-label') || '';
      const jumlahSeries = el.getAttribute('data-jumlah-series') || 10;
      const jumlahChapter = el.getAttribute('data-jumlah-chapter') || 3;
      const nameSeries = el.getAttribute('data-series');

      const posts = storageType.getItem(nameSeries)
        ? JSON.parse(storageType.getItem(nameSeries))
        : [];

      if (posts.length == 0) {
        const seriesList = await new Promise((resolve) => {
          const bS = new BloggerScript({
            host: config.host,
            jumlah: jumlahSeries,
            q: `label:"${config.seriesLabel}"${
              customLabel ? ` + label:"${customLabel}"` : ''
            }`,
            orderby: 'updated',
            sizeImage: config.imageSize,
          });
          bS.run((post) => {
            resolve(post);
          });
        });

        const getChapters = await new Promise(async (resolve) => {
          const arr = [];
          for (post of seriesList) {
            const cekLabel = getLabelTitle(post.label, post.title);

            const chp = chapters.items.filter((chapter) =>
              chapter.label.some((k) => k == cekLabel)
            );
            post['chapters'] = chp.slice(0, jumlahChapter);
            if (
              chp.length < jumlahChapter &&
              chapters.items.length < chapters.max
            ) {
              const getCh = await new Promise((resolve) => {
                const bS = new BloggerScript({
                  jumlah: jumlahChapter + 1,
                  label: cekLabel,
                  host: config.host,
                });
                bS.run((entry) => {
                  entry.forEach((p) => {
                    if (
                      p.label.some((v) => v == config.chapterLabel) &&
                      !chapters.items.some((i) => i.id == p.id)
                    ) {
                      const data = {
                        title: p.title,
                        link: p.link,
                        id: p.id,
                        label: p.label,
                        date: p.date,
                        published: p.published
                      };
                      chapters.items.push(data);
                      if (post['chapters'].length < jumlahChapter)
                        post['chapters'].push(data);
                    }
                  });
                  resolve();
                });
              });
            }
            arr.push(post);
            posts.push(post);
          };
          resolve(arr);
        });

        storageType.setItem(nameSeries, JSON.stringify(getChapters));
      }

      createSeriesWithChapter(posts, el);
    }
    storageType.setItem('chapterArr', JSON.stringify(chapters));
  })();
})(document, window);

