      let multiTab = new BloggerScript({
  allRandom: true,
  jumlah: 6,
  label: ["Action", "Adventure", "Comedy", "Drama", "Ecchi", "Fantasy", "Game", "Harem", "Historical", "Isekai", "Horror", "Josei", "Magic", "Mecha", "Military", "Music", "Mystery", "Parody", "Police", "Psychological", "Romance", "Samurai", "School", "Sci-Fi", "Seinen", "Shoujo", "Shounen", "Space", "Sports", "Super Power", "Supernatural", "Thriller", "Vampire", "Yuri"],
  maxTab: 3,
  noImage: '',
  sizeImage: 'w100-rw-h142'
})

function _multiTab(e) {
  let obj = {};
  multiTab.config.label.filter(label => {
    e.filter(item => {
      let check = item.label.some(s => s == label);
      if (check) {
        if (obj[label]) {
          obj[label].push(item)
        } else {
          obj[label] = [item];
        }
      }
    })
  })
  window.matchMedia("(max-width: 600px)").matches && (multiTab.config.maxTab = 3);
  window.matchMedia("(max-width: 480px)").matches && (multiTab.config.maxTab = 2);
  let html = '<div class="series-gen relative loading">';
  let nav = '<div class="flex items-center justify-end mb-4"><ul class="nav-tabs tabs tab flex items-center bg-(--tint-94) dark:bg-(--shade-90) rounded-full">';
  let inner = '<div class="listupd">';
  let post = '';
  let label = multiTab.shuffle(Object.keys(obj)).slice(0, multiTab.config.maxTab).sort().reduce((t, e) => (t[e] = obj[e], t), {});
  for (const key in label) {
    if (Object.hasOwnProperty.call(label, key)) {
      const item = multiTab.shuffle(label[key]).slice(0, multiTab.config.jumlah);
      nav += `<li class="cursor-pointer inline-block px-4 py-1 rounded-full hover:bg-(--accent) hover:text-white"><span data-id="series-${key.replace(/\W/g, '').toLowerCase()}">${key}</span></li>`;
      post += `<div id="series-${key.replace(/\W/g, '').toLowerCase()}" class="customAF RSf6 scroll"><div class="scrolling inner gap-x-4 gap-y-1 grid">`

      item.forEach(i => {
        let title = i.title;
        let score = i.label.find(i => !/[^0-9\.-]+/g.test(i)) || i.label.find(i => i.includes('Score'))?.split(' ')[1] || '0.0';
        let link = i.link;
        let status = i.label.find(i => ['Ongoing', 'Completed', 'Dropped', 'Cooming Soon'].includes(i)) || "";
        let tipe = i.label.find(i => ['Manga', 'Manhwa', 'Manhua', 'Novel', 'Doujin', 'LN', 'WN'].includes(i)) || "";
        let image = i.image;
		let doujin = i.label.find(i => ['Doujin'].includes(i)) || false;
		let nsfw = i.label.find(i => ['NSFW'].includes(i)) || "";

        post += `<article class="customAgata px-4 rounded-lg items-center bg-(--tint-94) shadow dark:bg-(--shade-90) ${nsfw.toLowerCase()}"><a class="grid items-center tempt grid-cols-[auto_60px_1fr] gap-x-2" href="${link}" title="${title}"><div class="number text-lg M1uq"></div>`;
        post += `<div class="images relative overflow-hidden">`;
		tipe && (post += `<span class="absolute rounded top-1 end-1 text-[0.6rem] px-1 ${tipe.toLowerCase()}">${tipe}</span>`);
        post += `<img class="rounded-lg" src="${image}" alt="${title}"/>`
		doujin && (post += ``);
        post += `</div>`;
        post += `<h2 class="entry-title line-clamp-2 h-min font-medium"></span>${title}</h2>`;
        post += `<div class="rating flex items-center justify-between text-xs"><span data-name="${status}">${status}</span><div class="rating-prc"><div class="rtp"><div class="rtb">`;
        post += `<span style="width:${score.length == 4 ? score == '10.0' ? score.replace('.', '') : score.replace('.', '').slice(0, -1) : score.replace('.', '')}%"></span>`;
        post += `</div></div><div class="flex items-center" content="${score}"><svg aria-hidden="true" class="w-4 h-4 text-accent" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><title>First star</title><path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z"></path></svg>${score}</div></div></div>`
        post += `</a></article>`;
      })

      post += '</div></div>';
    }
  }
  html += `${nav}</ul></div>${inner + post}</div></div>`;
  document.getElementById('mTP_Slider').innerHTML = html;
  let se = document.querySelectorAll('[data-id*=series');
  se.forEach(i => {
    i.parentElement.addEventListener('click', e => {
      let id = e.currentTarget.querySelector('span').dataset.id;
      se.forEach(l => l.parentElement.classList.remove('active'));
      e.currentTarget.classList.add('active');
      document.querySelectorAll('.customAF.scroll').forEach(l => l.classList.remove('active'));
      document.getElementById(id).classList.add('active');
    })
  })
  se[0].click();
};

multiTab.randomPost('/feeds/posts/summary/-/Series', _multiTab);
