(function (a, b) {
function isHtml(e) {
    let html = '<div class="grid relative overflow-hidden"><ul class="grid gap-x-2">';

    if (e.length != 0) {
    e.forEach((post) => {
        let title = post.title,
        time = timeAgo(new Date(post.updated)),
        chapterTitle = title.match(/(chapter\s\d{1,4}.?\d{1,2})/gi),
        link = post.link,
        commentsCount = post.comments_count,
        image = post.image;

        !chapterTitle && (chapterTitle = title);

        html += `
        <li class="border-t dark:border-gray-700 group items-center grid grid-cols-[1fr_70px_100px_1fr_auto]">
            <a class="py-3 w-full link-effect-no-ring dark:visited:text-gray-500 visited:text-gray-400 text-black dark:text-gray-200 flex items-center active:bg-blue-500/30 hover:bg-gray-100 dark:hover:bg-gray-700 gap-4" href="${link}" alt="${title}" title="${title}">
                <div class="border inline-table border-white/10 bg-(--tint-98)/10 overflow-hidden rounded-xl">
                <div class="aspect-square w-5 bg-(--tint-98)/10 bg-cover bg-center rounded-lg" style="background-image:url(${image})"></div>
                </div>
                <span class="flex gap-1 justify-start items-center overflow-hidden">
                    <span class="text-sm truncate">${chapterTitle}</span>
                    ${['seconds', 'minutes', 'hours'].some(i => time.includes(i)) ? 
                    `<span class="text-xs ml-0.5 bg-(--tint-98)/20 group-hover:bg-(--tint-98)/30 transition-all px-1 rounded">New</span>` 
                    : ''
                    }
                </span>
            </a>
                    <div>
                    ${commentsCount != 0 ? `
                    <div class="flex items-center justify-start gap-1.5 -ml-0.5">
                        <div class="flex gap-1 justify-center items-center w-fit bg-yellow-200 text-yellow-600 border-yellow-200 pl-1 pr-1.5 h-5 bg-opacity-90 rounded-full">
                        <div class="w-5 h-fit">
                            <svg xmlns="http://www.w3.org/2000/svg" width="24px" height="24px" viewBox="0 0 1024 1024">
                            <path fill="currentColor" d="M573 421c-23.1 0-41 17.9-41 40s17.9 40 41 40c21.1 0 39-17.9 39-40s-17.9-40-39-40m-280 0c-23.1 0-41 17.9-41 40s17.9 40 41 40c21.1 0 39-17.9 39-40s-17.9-40-39-40"></path>
