/**/
    window.clwd = {
        arr: [],
        filterLabels: ['Series'],
        bScript: null,

        // Modified method to handle parallel loading
        async loadAllPosts(label) {
            this.bScript = new BloggerScript({
                'host': 'www.re-yume.my.id',
                'label': label,
                'max-results': 150,
                'feed': 'summary',
                'alt': 'json-in-script'
            });

            // First request to get total count
            const initialPosts = await new Promise(resolve => {
                const url = this.bScript.createURL().href;
                const xhr = this.bScript.getXHRtype(url);
                this.bScript[xhr](url, (response) => {
                    resolve(response);
                });
            });

            const totalItems = initialPosts.feed?.openSearch$totalResults.$t || 0;
            const batchSize = 150;
            const requests = [];

            // Create parallel requests for all pages
            for (let startIndex = 1; startIndex <= totalItems; startIndex += batchSize) {
                this.bScript.config['start-index'] = startIndex;
                const url = this.bScript.createURL().href;
                const xhr = this.bScript.getXHRtype(url);

                requests.push(new Promise(resolve => {
                    this.bScript[xhr](url, (response) => {
                        const feed = this.bScript.getFeed(response);
                        resolve(feed);
                    });
                }));
            }

            // Execute all requests in parallel
            const results = await Promise.all(requests);

            // Combine all results
            return results.flat();
        },

        updateSort(sortBy) {
            if (!this.arr.length) return;
            this.arr = this.bScript.sort(this.arr, sortBy);
            this.compile();
        },

        showLoading(show) {
            document.getElementById('loadingIndicator').style.display = show ? 'block' : 'none';
            document.getElementById('clwd').style.display = show ? 'none' : 'block';
        },

        compile() {
            // Filter out series posts
            this.arr = this.arr.filter(item => {
                const hasFilteredLabel = item.label ? 
                    !item.label.some(label => this.filterLabels.includes(label)) : true;
                return hasFilteredLabel;
            });

            // Keep track of array length for first/last chapter
            const totalItems = this.arr.length;

            // Create HTML structure
            let n = `<ul class="bg-base-100 flex flex-col">`;
            let s = '<div class="grid grid-cols-2 gap-2">';

            // Generate chapter list
            this.arr.forEach((item, index) => {
                // Set first chapter (oldest)
                if (index === totalItems - 1) {
                    document.getElementById("first").innerHTML = 
                        `<a class="rounded p-4 text-center text-sm font-semibold bg-(--colors-primary-200) text-(--colors-primary-700) hover:bg-primary-100 hover:text-(--colors-highlight)" href="${item.link}">Start Reading</a>`;
                }

                // Set latest chapter
                if (index === 0) {
                    document.getElementById("last").innerHTML = 
                        `<a class="rounded p-4 text-center text-sm font-semibold bg-(--colors-primary-200) text-(--colors-primary-700) hover:bg-primary-100 hover:text-highlight" href="${item.link}">Latest Chapter</a>`;
                }

                // Add chapter to list
                n += `<li class="relative odd:bg-gray-400 dark:odd:bg-gray-700">
                            <a class="eph-num flex py-2 px-2.5 transition-all focus:ring-2 focus:ring-accent duration-300 bg-(--tint-94) dark:bg-(--shade-90)/50 hover:bg-gray-300 dark:hover:bg-gray-700" href="${item.link}">
<span class="vcn me-2"><svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 256 256"><g fill="currentColor"><path d="M232 56v144a16 16 0 0 1-16 16H40a16 16 0 0 1-16-16V56a16 16 0 0 1 16-16h176a16 16 0 0 1 16 16Z" opacity=".2"></path><path d="m205.66 85.66l-96 96a8 8 0 0 1-11.32 0l-40-40a8 8 0 0 1 11.32-11.32L104 164.69l90.34-90.35a8 8 0 0 1 11.32 11.32Z"></path></g></svg></span>
                                <span class="chapternum">
                                    ${item.title}
                                </span>
                                <span class="ms-auto chapterdate text-gray-400 dark:text-gray-600">
                                        ${item.date}
                                </span>
                            </a>
                </li>`;
            });

            s += "</div>";
            n += "</ul>";

            // Update DOM
            document.getElementById("clwd").innerHTML = `${s+n}`;
            document.getElementById("chapternumber").innerHTML = this.arr.length;
        },

        async run(label) {
            this.showLoading(true);

            try {
                // Load all posts using pagination
                const posts = await this.loadAllPosts(label);

                // Filter and sort posts
                this.arr = posts.filter(item => {
                    const hasFilteredLabel = item.label ? 
                        !item.label.some(label => this.filterLabels.includes(label)) : 
                        true;
                    return hasFilteredLabel;
                });

                // Initial sort
                this.updateSort('Z-A');

                console.log(`Total chapters loaded: ${this.arr.length}`);
            } catch (error) {
                console.error('Error loading chapters:', error);
            } finally {
                this.showLoading(false);
            }
        }
    };

    // Keep existing initialization code
    function initChapterList() {
        const clwdDiv = document.getElementById('clwd');
        if (clwdDiv) {
            const scriptContent = clwdDiv.querySelector('script');
            if (scriptContent) {
                const match = scriptContent.innerHTML.match(/clwd\.run\(['"](.*?)['"]\)/);
                if (match && match[1]) {
                    clwd.run(match[1]);
                }
            }
        }
    }

    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', initChapterList);
    } else {
        initChapterList();
    }
