//
// Ganti turbo:load ke DOMContentLoaded agar lebih universal
document.addEventListener("DOMContentLoaded", () => {
    const mainElement = document.querySelector(".addHistory");

    if (mainElement) {
        // Pastikan semua atribut ada
        const id = mainElement.getAttribute("data-id");
        const title = mainElement.getAttribute("data-title");
        const url = mainElement.getAttribute("data-url");
        const img = mainElement.getAttribute("data-img");
        const label = mainElement.getAttribute("data-label");

        // Jangan simpan jika ada data yang kosong
        if (id && title && url && img && label) {
            const historyData = {
                id,
                title,
                url,
                img,
                label,
                timestamp: new Date().toLocaleString()
            };

            let readingHistory = JSON.parse(localStorage.getItem("readingHistory")) || [];

            // Cek duplikat berdasarkan id
            if (!readingHistory.some(item => item.id === historyData.id)) {
                readingHistory.unshift(historyData); // unshift agar terbaru di atas
                localStorage.setItem("readingHistory", JSON.stringify(readingHistory));
            }
        }
    }
});
