// Ganti turbo:load ke DOMContentLoaded agar lebih universal
document.addEventListener("DOMContentLoaded", () => {
    const limitHistory = false; // Ubah ke angka jika ingin membatasi

    function renderReadingHistory() {
        const historyList = document.querySelector(".activity-feed");
        if (!historyList) return;
        historyList.innerHTML = "";

        const readingHistory = JSON.parse(localStorage.getItem("readingHistory")) || [];
        let limitedHistory = readingHistory;
        if (limitHistory) {
            limitedHistory = readingHistory.slice(0, limitHistory);
        }

        limitedHistory.forEach(item => {
            const idnumber = item.id;
            const listItem = document.createElement("li");
            listItem.classList.add("list", "wrap", "group", "bg-[rgb(var(--color-foreground))]", "rounded-[4px]", "text-[0.8125rem]", "relative");
            listItem.setAttribute("data-id", idnumber);

            listItem.innerHTML = `
