            // Menambahkan elemen baru ke bagian atas daftar
            historyList.insertAdjacentElement("beforeend", listItem);
        });

        // Jika tidak ada item yang ditampilkan, bisa memberikan pesan
        if (limitedHistory.length === 0) {
            const emptyMessage = document.createElement("li");
            emptyMessage.textContent = "No reading history available.";
            historyList.appendChild(emptyMessage);
        }
    }

    // Fungsi untuk menghapus semua riwayat
    function clearReadingHistory() {
        localStorage.removeItem("readingHistory"); // Hapus dari localStorage
        renderReadingHistory(); // Perbarui tampilan setelah penghapusan
    }

    // Pastikan tombol clear-history ada sebelum menambah event listener
    const clearBtn = document.getElementById("clear-history");
    if (clearBtn) {
        clearBtn.addEventListener("click", clearReadingHistory);
    }

    // Event delegation untuk hapus per item
    document.querySelector(".activity-feed").addEventListener("click", function(e) {
        const target = e.target.closest(".delete-history-item");
        if (target) {
            const id = target.getAttribute("data-id");
            let readingHistory = JSON.parse(localStorage.getItem("readingHistory")) || [];
            readingHistory = readingHistory.filter(item => item.id !== id);
            localStorage.setItem("readingHistory", JSON.stringify(readingHistory));
            renderReadingHistory();
        }
    });

    renderReadingHistory();
});
